import React, { useState } from 'react';
import WorkoutTracker from './WorkoutTracker';
import { useNavigate } from 'react-router-dom';

const WorkoutPage = () => {
  const navigate = useNavigate();
  const [repCount, setRepCount] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [currentExercise, setCurrentExercise] = useState('pushups');

  // Called when Finish Workout is clicked
  const handleFinishWorkout = () => {
    alert('Workout Complete!');
    // maybe navigate to summary or next exercise
  };

  // Called when Restart → Delete is clicked
  const handleRestartWorkout = () => {
    // reset state for new session
    setRepCount(0);
    setFeedback('');
    setCurrentExercise('pushups'); // or first exercise
  };

  // Called when Restart → See Results is clicked
  const handleSeeResults = () => {
    navigate('/workout-summary'); // your summary page route
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: '50px' }}>
      <WorkoutTracker
        repCount={repCount}
        feedback={feedback}
        exercise={currentExercise}
        onDone={handleFinishWorkout}
        onRestart={handleRestartWorkout}   // ✅ pass it here
        onSeeResults={handleSeeResults}    // ✅ pass it here
      />
    </div>
  );
};

export default WorkoutPage;
