import React, { useEffect, useRef, useState } from 'react';
import dumbbellImg from './components/dumbbell.png';
import './DumbbellBackground.css';

export default function DumbbellBackground() {
  const [mouse, setMouse] = useState({ x: -9999, y: -9999 });
  const activeMap = useRef({}); // track which dumbbells are "cooling down"

  const dumbbells = [];
  const rows = 32;
  const cols = 60;
  const spacing = 55; // ðŸ‘ˆ closer together

  useEffect(() => {
    const handleMove = (e) => {
      setMouse({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMove);
    return () => window.removeEventListener('mousemove', handleMove);
  }, []);

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col < cols; col++) {
      dumbbells.push({
        id: `${row}-${col}`,
        top: row * spacing + (Math.random() * 16 - 8),
        left: col * spacing + (Math.random() * 16 - 8),
      });
    }
  }

  return (
    <div className="dumbbell-background">
      {dumbbells.map((d) => {
        const dx = mouse.x - d.left;
        const dy = mouse.y - d.top;
        const distance = Math.sqrt(dx * dx + dy * dy);

        const isWithinRadius = distance < 110;
        const isActive = activeMap.current[d.id];

        if (isWithinRadius && !isActive) {
          activeMap.current[d.id] = true;

          // turn it back off after 1 second
          setTimeout(() => {
            activeMap.current[d.id] = false;
          }, 1000);
        }

        return (
          <img
            key={d.id}
            src={dumbbellImg}
            alt="dumbbell"
            className={`dumbbell ${isActive ? 'active' : ''}`}
            style={{
              top: `${d.top}px`,
              left: `${d.left}px`,
            }}
          />
        );
      })}
    </div>
  );
}
