import React, { useEffect, useState } from 'react';
import dumbbellImg from './components/dumbbell.png'; // <- make sure this path is correct

const DumbbellBackground = () => {
  const [dumbbells, setDumbbells] = useState([]);
  const rows = 10;
  const cols = 10;
  const spacing = 80;

  useEffect(() => {
    const arr = [];
    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        const offsetX = row % 2 === 0 ? 0 : spacing / 2;
        arr.push({
          id: `${row}-${col}`,
          top: row * spacing,
          left: col * spacing + offsetX,
        });
      }
    }
    setDumbbells(arr);
  }, []);

  return (
    <div className="dumbbell-background" style={{ position: 'absolute', inset: 0, overflow: 'hidden', zIndex: 0 }}>
      <style>{`
        @keyframes scrollLeft {
          0% { transform: translateX(0); }
          100% { transform: translateX(-${spacing}px); }
        }
        .dumbbell-container {
          animation: scrollLeft 10s linear infinite;
          position: relative;
        }
      `}</style>

      <div className="dumbbell-container">
        {dumbbells.map((d) => (
          <img
            key={d.id}
            src={dumbbellImg}
            alt="dumbbell"
            style={{
              position: 'absolute',
              width: 40,
              height: 40,
              top: d.top,
              left: d.left,
              opacity: 0.3,
              transform: 'rotate(45deg)',
            }}
          />
        ))}
      </div>

      {/* duplicate layer for seamless scroll */}
      <div className="dumbbell-container" style={{ transform: `translateX(${spacing}px)` }}>
        {dumbbells.map((d) => (
          <img
            key={`dup-${d.id}`}
            src={dumbbellImg}
            alt="dumbbell"
            style={{
              position: 'absolute',
              width: 40,
              height: 40,
              top: d.top,
              left: d.left,
              opacity: 0.3,
              transform: 'rotate(45deg)',
            }}
          />
        ))}
      </div>
    </div>
  );
};

export default DumbbellBackground;
