import React, { useEffect, useState } from 'react';
import dumbbellImg from './components/dumbbell.png';
import './DumbbellBackground.css';

export default function DumbbellBackground() {
  const [mouse, setMouse] = useState({ x: -9999, y: -9999 });

  const dumbbells = [];
  const rows = 28;
  const cols = 50;
  const spacing = 70;

  useEffect(() => {
    const handleMove = (e) => {
      setMouse({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMove);
    return () => window.removeEventListener('mousemove', handleMove);
  }, []);

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col < cols; col++) {
      const randomX = Math.random() * 30 - 15;
      const randomY = Math.random() * 30 - 15;

      dumbbells.push({
        id: `${row}-${col}`,
        top: row * spacing + randomY,
        left: col * spacing + randomX,
      });
    }
  }

  return (
    <div className="dumbbell-background">
      {dumbbells.map((d) => {
        const dx = mouse.x - d.left;
        const dy = mouse.y - d.top;
        const distance = Math.sqrt(dx * dx + dy * dy);

        const isActive = distance < 120;

        return (
          <img
            key={d.id}
            src={dumbbellImg}
            alt="dumbbell"
            className={`dumbbell ${isActive ? 'active' : ''}`}
            style={{
              top: `${d.top}px`,
              left: `${d.left}px`,
            }}
          />
        );
      })}
    </div>
  );
}
