import React, { useEffect, useState } from 'react';
import dumbbellImg from './components/dumbbell.png'; // Make sure the image exists here
import './DumbbellBackground.css';

const DumbbellBackground = () => {
  const [dumbbells, setDumbbells] = useState([]);

  const rows = 10; // number of rows
  const cols = 10; // number of columns
  const spacing = 80; // px between dumbbells

  useEffect(() => {
    const temp = [];
    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        const offsetX = row % 2 === 0 ? 0 : spacing / 2;
        temp.push({
          id: `${row}-${col}`,
          top: row * spacing,
          left: col * spacing + offsetX,
          speed: 20 + Math.random() * 20, // random speed for subtle variation
        });
      }
    }
    setDumbbells(temp);
  }, []);

  return (
    <div className="dumbbell-background">
      {dumbbells.map((dumbbell) => (
        <img
          key={dumbbell.id}
          src={dumbbellImg}
          alt="dumbbell"
          className="dumbbell"
          style={{
            top: dumbbell.top,
            left: dumbbell.left,
            animationDuration: `${dumbbell.speed}s`,
          }}
        />
      ))}
    </div>
  );
};

export default DumbbellBackground;
