import React, { useState } from 'react';
import './App.css';
import ExerciseSelector from './components/ExerciseSelector';
import CameraFeed from './components/CameraFeed';
import WorkoutTracker from './components/WorkoutTracker';

function App() {
  const [selectedExercise, setSelectedExercise] = useState(null);
  const [videoFeed, setVideoFeed] = useState(null);
  const [repCount, setRepCount] = useState(0);
  const [feedback, setFeedback] = useState('');

  const handleSelectExercise = (exercise) => {
    setSelectedExercise(exercise);
  };

  const handleVideoFeed = (videoElement) => {
    setVideoFeed(videoElement);
  };

  const handleGoBack = () => {
    setSelectedExercise(null);
    setVideoFeed(null);
    setRepCount(0);
    setFeedback('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Personal Trainer</h1>
      </header>
      <main>
        {!selectedExercise ? (
          <ExerciseSelector onSelectExercise={handleSelectExercise} />
        ) : (
          <div className="workout-container">
            <div className="camera-container">
              <CameraFeed onVideoFeed={handleVideoFeed} />
              <button onClick={handleGoBack} className="back-button">Back</button>
            </div>
            <WorkoutTracker repCount={repCount} feedback={feedback} />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
