import React, { useEffect, useState } from 'react';
import dumbbellImg from './components/dumbbell.png';


const DumbbellBackground = () => {
  const [dumbbells, setDumbbells] = useState([]);

  const rows = 10;   // fewer rows for performance
  const cols = 20;   // fewer columns
  const spacing = 60;

  useEffect(() => {
    const temp = [];
    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        const offsetX = row % 2 === 0 ? 0 : spacing / 2;
        temp.push({
          id: `${row}-${col}`,
          top: row * spacing,
          left: col * spacing + offsetX,
        });
      }
    }
    setDumbbells(temp);
  }, []);

  return (
    <>
      <style>{`
        @keyframes scrollLeft {
          0% { transform: translateX(0); }
          100% { transform: translateX(-${spacing}px); }
        }
        .dumbbell-layer {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          animation: scrollLeft 5s linear infinite;
          pointer-events: none;
          z-index: 0;
        }
      `}</style>

      <div className="dumbbell-layer">
        {dumbbells.map(d => (
          <img
            key={d.id}
            src={dumbbellImg}
            alt="dumbbell"
            style={{
              position: 'absolute',
              top: d.top,
              left: d.left,
              width: 40,
              height: 40,
              opacity: 0.2,
              transform: 'rotate(45deg)',
            }}
          />
        ))}
      </div>

      {/* duplicate layer for seamless scroll */}
      <div className="dumbbell-layer" style={{ transform: `translateX(${spacing}px)` }}>
        {dumbbells.map(d => (
          <img
            key={`dup-${d.id}`}
            src={dumbbellImg}
            alt="dumbbell"
            style={{
              position: 'absolute',
              top: d.top,
              left: d.left,
              width: 40,
              height: 40,
              opacity: 0.2,
              transform: 'rotate(45deg)',
            }}
          />
        ))}
      </div>
    </>
  );
};

export default DumbbellBackground;
