import React from 'react';
import './ExerciseSelector.css';

const ExerciseSelector = ({ onSelectExercise, onSelectGame }) => {
  const exercises = [
    { 
      id: 'pushups', 
      name: 'Push-Ups',
      description: 'Build upper body strength'
    },
    { 
      id: 'pullups', 
      name: 'Pull-Ups',
      description: 'Master your back and arms'
    },
    {
      id: 'squats',
      name: 'Squats',
      description: 'Strengthen your legs and core'
    }
  ];

  return (
    <div className="exercise-selector">
      <div className="selector-content">

        {/* FORM TRACKER SECTION */}
        <div className="section-container">
          <h2 className="section-title">Form Tracker</h2>
          <p className="section-subtitle">Track your reps with AI form analysis</p>
          <div className="exercise-grid">
            {exercises.map((exercise) => (
              <button 
                key={exercise.id} 
                className="exercise-card"
                onClick={() => onSelectExercise(exercise.id)}
              >
                <h3 className="exercise-name">{exercise.name}</h3>
                <p className="exercise-description">{exercise.description}</p>
              </button>
            ))}
          </div>
        </div>

        {/* MOVEMENT CHALLENGE SECTION */}
        <div className="section-container">
          <h2 className="section-title">Movement Challenge</h2>
          <p className="section-subtitle">Control the game with your body</p>
          <div className="exercise-grid">
            {exercises.map((exercise) => (
              <button 
                key={`game-${exercise.id}`}
                className="exercise-card game-card"
                onClick={() => onSelectGame(exercise.id)}
              >
                <h3 className="exercise-name">{exercise.name}</h3>
              </button>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
};

export default ExerciseSelector;
