import React from 'react';
import './WorkoutTracker.css';

const WorkoutTracker = ({ repCount, feedback, exercise, onDone }) => {
  const exerciseName = exercise === 'pushups' ? 'Push-Ups' : 'Pull-Ups';

  const formatFeedback = (feedbackString) => {
    if (!feedbackString) return 'Perfect form! ðŸŽ¯';

    const feedbackMap = {
      // Push-up feedback - CLEARLY DIFFERENT
      'arms_barely_bent': 'âš ï¸ Arms barely bent at all',
      'extremely_shallow': 'âŒ Way too shallow',
      'too_shallow': 'ðŸ“‰ Much deeper needed',
      'bit_shallow': 'ðŸ“ A bit more depth',
      'barely_moved_down': 'âš ï¸ Almost no movement',
      'need_more_depth': 'â¬‡ï¸ Need significantly more depth',
      'increase_depth': 'ðŸ“Š Could increase depth',
      'slight_depth_issue': 'âœ“ Nearly perfect depth',
      'hips_dropped': 'â¬†ï¸ Hips dropped way down',
      'hips_sagging': 'ðŸ’ª Hips sagging, engage core',
      'weak_core': 'ðŸŽ¯ Tighten your core slightly',
      'knees_very_bent': 'ðŸ¦µ Knees are very bent',
      'knees_bent': 'ðŸ¦µ Straighten your legs',
      'legs_not_straight': 'ðŸ“ Legs almost straight',
      'too_fast': 'ðŸŒ Slow down the movement',

      // Pull-up feedback
      'too_fast_bouncing': 'âš ï¸ Stop bouncing, control it',
      'no_chin_clearance': 'â¬†ï¸ Chin needs to clear bar',
      'partial_clearance': 'ðŸ“ Pull higher',
      'minimal_elbow_bend': 'ðŸ’ª Bend elbows much more',
      'partial_elbow_bend': 'ðŸ’ª Incomplete pull',
      'kipping': 'ðŸš« No swinging or kipping',
      'excessive_hip_bend': 'ðŸ“ Too much hip movement',
      'slight_hip_bend': 'ðŸ“ Keep body straighter',
      'knees_bent': 'ðŸ¦µ Keep legs straight',
      'slight_knee_bend': 'ðŸ¦µ Straighten legs more'
    };

    const flags = feedbackString.split(', ');
    return flags.map(flag => feedbackMap[flag] || flag).join(' â€¢ ');
  };

  return (
    <div className="workout-tracker">
      <div className="tracker-header">
        <h3>{exerciseName}</h3>
      </div>

      <div className="rep-counter">
        <div className="rep-label">REPS</div>
        <div className="rep-number">{repCount}</div>
      </div>

      <div className="feedback-section">
        <div className="feedback-label">FORM CHECK</div>
        <div className={`feedback-text ${feedback ? 'has-feedback' : 'good-form'}`}>
          {formatFeedback(feedback)}
        </div>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">ðŸ”¥</div>
          <div className="stat-value">{repCount * (exercise === 'pushups' ? 5 : 8)}</div>
          <div className="stat-label">Calories</div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">âš¡</div>
          <div className="stat-value">{Math.floor(repCount / 5)}</div>
          <div className="stat-label">Sets</div>
        </div>
      </div>

      <button className="done-button" onClick={onDone}>
        Finish Workout
      </button>
    </div>
  );
};

export default WorkoutTracker;
