import React from 'react';
import dumbbellImg from 'figma:asset/6a5369bccbe0dbee0f04eb065b638514b65cddb8.png';

export default function DumbbellBackground() {
  const dumbbells = [];
  const rows = 30;
  const cols = 60;
  const spacing = 50;

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col < cols; col++) {
      const offsetX = row % 2 === 0 ? 0 : spacing / 2;
      dumbbells.push({
        id: `${row}-${col}`,
        top: row * spacing,
        left: col * spacing - spacing * 2 + offsetX, // Start slightly left
      });
    }
  }

  const dumbbellStyle = {
    width: '32px',
    height: '32px',
    objectFit: 'contain',
    opacity: 0.3,
    transform: 'rotate(45deg)',
    filter: 'brightness(0.5) contrast(1.2)',
    position: 'absolute',
  };

  return (
    <>
      <style>{`
        @keyframes scrollRight {
          0% { transform: translateX(-${spacing}px); }
          100% { transform: translateX(0); }
        }
        .dumbbell-container {
          animation: scrollRight 3s linear infinite;
          position: absolute;
          inset: 0;
        }
      `}</style>

      {/* Main layer */}
      <div className="dumbbell-container">
        {dumbbells.map((d) => (
          <img
            key={d.id}
            src={dumbbellImg}
            alt="dumbbell"
            style={{ ...dumbbellStyle, top: `${d.top}px`, left: `${d.left}px` }}
          />
        ))}
      </div>

      {/* Duplicate layer for seamless scroll */}
      <div className="dumbbell-container" style={{ transform: `translateX(${spacing}px)` }}>
        {dumbbells.map((d) => (
          <img
            key={`dup-${d.id}`}
            src={dumbbellImg}
            alt="dumbbell"
            style={{ ...dumbbellStyle, top: `${d.top}px`, left: `${d.left}px` }}
          />
        ))}
      </div>
    </>
  );
}
