import React from 'react';
import './WorkoutTracker.css';

const WorkoutTracker = ({ repCount, feedback, exercise, onDone }) => {
  let exerciseName;
  switch (exercise) {
    case 'pushups':
      exerciseName = 'Push-Ups';
      break;
    case 'pullups':
      exerciseName = 'Pull-Ups';
      break;
    case 'squats':
      exerciseName = 'Squats';
      break;
    default:
      exerciseName = 'Exercise';
  }

  const formatFeedback = (feedbackString) => {
    if (!feedbackString) return 'Perfect form!';
    return feedbackString.replace(/,/g, ' •');
  };

  return (
    <div className="workout-tracker">
      <div className="tracker-header">
        <h3>{exerciseName}</h3>
      </div>

      <div className="rep-counter">
        <div className="rep-label">REPS</div>
        <div className="rep-number">{repCount}</div>
      </div>

      <div className="feedback-section">
        <div className="feedback-label">FORM CHECK</div>
        <div className={`feedback-text ${feedback ? 'has-feedback' : 'good-form'}`}>
          {formatFeedback(feedback)}
        </div>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-value">{repCount * (exercise === 'pushups' ? 5 : 8)}</div>
          <div className="stat-label">Calories</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{Math.floor(repCount / 5)}</div>
          <div className="stat-label">Sets</div>
        </div>
      </div>

      <button className="done-button" onClick={onDone}>
        Finish Workout
      </button>
    </div>
  );
};

export default WorkoutTracker;
