import React, { useEffect, useState, useRef } from 'react';
import './PositionBar.css';

const PositionBar = ({ landmarks, exercise, repActive }) => {
  const [position, setPosition] = useState(50); // 0-100%
  const [phase, setPhase] = useState('up');

  const repStartRef = useRef(null);    // shoulder/hip Y at start of rep
  const repMinRef = useRef(null);      // lowest Y observed in current rep
  const repMaxRef = useRef(null);      // highest Y observed in current rep

  useEffect(() => {
    if (!landmarks || landmarks.length === 0) return;

    // --- Get current tracking point ---
    let currentY;
    if (exercise === 'pushups' || exercise === 'pullups') {
      const leftShoulder = landmarks[11];
      const rightShoulder = landmarks[12];
      if (!leftShoulder || !rightShoulder) return;
      currentY = (leftShoulder.y + rightShoulder.y) / 2;
    } else if (exercise === 'squats') {
      const leftHip = landmarks[23];
      const rightHip = landmarks[24];
      if (!leftHip || !rightHip) return;
      currentY = (leftHip.y + rightHip.y) / 2;
    } else return;

    // --- If new rep started, reset min/max ---
    if (repActive && repStartRef.current === null) {
      repStartRef.current = currentY;
      repMinRef.current = currentY;
      repMaxRef.current = currentY;
    }

    // --- Update min/max during rep ---
    if (repActive) {
      repMinRef.current = Math.min(repMinRef.current, currentY);
      repMaxRef.current = Math.max(repMaxRef.current, currentY);

      // Map currentY to 0-100% based on this rep's motion
      const range = repMaxRef.current - repMinRef.current || 0.001;
      let pct = ((repMaxRef.current - currentY) / range) * 100;
      pct = Math.max(0, Math.min(100, pct));
      setPosition(pct);

      setPhase(pct < 40 ? 'down' : 'up');
    } else {
      // rep ended: reset refs
      repStartRef.current = null;
      repMinRef.current = null;
      repMaxRef.current = null;
      setPosition(50); // idle position
      setPhase('up');
    }
  }, [landmarks, exercise, repActive]);

  return (
    <div className="position-bar-container">
      <div className="position-bar">
        <div className="bar-track">
          <div className="target-zone top-zone"></div>
          <div className="target-zone bottom-zone"></div>
          <div className={`position-indicator ${phase}`} style={{ bottom: `${position}%` }}>
            <div className="indicator-dot"></div>
          </div>
        </div>
        <div className="bar-labels">
          <span className="label-top">UP</span>
          <span className="label-bottom">DOWN</span>
        </div>
      </div>
    </div>
  );
};

export default PositionBar;
