import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';
import ExerciseSelector from './components/ExerciseSelector';
import CameraFeed from './components/CameraFeed';
import WorkoutTracker from './components/WorkoutTracker';

function App() {
  const [selectedExercise, setSelectedExercise] = useState(null);
  const [repCount, setRepCount] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [landmarks, setLandmarks] = useState([]);

  // Holds the actual Socket.IO client instance
  const socketRef = useRef(null);

  useEffect(() => {
    // Only create a socket when an exercise is selected
    if (!selectedExercise) {
      // If user goes back, clean up any existing socket
      if (socketRef.current) {
        socketRef.current.disconnect();
        socketRef.current = null;
      }
      return;
    }

    // Avoid creating multiple connections
    if (socketRef.current) {
      // Re-emit exercise if needed
      socketRef.current.emit('select_exercise', { exercise: selectedExercise });
      return;
    }

    const socket = io('http://localhost:8765');
    socketRef.current = socket;

    socket.on('connect', () => {
      console.log('Socket.IO connected, id =', socket.id);
      socket.emit('select_exercise', { exercise: selectedExercise });
    });

    socket.on('update', (data) => {
      console.log('Received data from backend:', data);
      setRepCount(data.rep_count);
      setFeedback(data.feedback);
      setLandmarks(data.landmarks || []);
    });

    socket.on('disconnect', () => {
      console.log('Socket.IO disconnected');
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
        socketRef.current = null;
      }
    };
  }, [selectedExercise]);

  const handleSelectExercise = (exercise) => {
    setSelectedExercise(exercise);
  };

  const handleGoBack = () => {
    setSelectedExercise(null);
    setRepCount(0);
    setFeedback('');
    setLandmarks([]);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Personal Trainer</h1>
      </header>
      <main>
        {!selectedExercise ? (
          <ExerciseSelector onSelectExercise={handleSelectExercise} />
        ) : (
          <div className="workout-container">
            <div className="camera-container">
              {/* Pass the actual socket instance */}
              <CameraFeed socket={socketRef.current} landmarks={landmarks} />
              <button onClick={handleGoBack} className="back-button">
                Back
              </button>
            </div>
            <WorkoutTracker repCount={repCount} feedback={feedback} />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
