import React, { useEffect, useState } from 'react';
import dumbbellImg from './components/dumbbell.png'; // make sure this path exists
import './DumbbellBackground.css';

const DumbbellBackground = () => {
  const [dumbbells, setDumbbells] = useState([]);
  const rows = 10;
  const cols = 10;
  const spacing = 80;

  useEffect(() => {
    const arr = [];
    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        const offsetX = row % 2 === 0 ? 0 : spacing / 2;
        arr.push({
          id: `${row}-${col}`,
          top: row * spacing,
          left: col * spacing + offsetX,
        });
      }
    }
    setDumbbells(arr);
  }, []);

  return (
    <div className="dumbbell-background">
      <div className="dumbbell-container">
        {dumbbells.map((d) => (
          <img
            key={d.id}
            src={dumbbellImg}
            alt="dumbbell"
            className="dumbbell-img"
            style={{ top: d.top, left: d.left }}
          />
        ))}
        {dumbbells.map((d) => (
          <img
            key={`dup-${d.id}`}
            src={dumbbellImg}
            alt="dumbbell"
            className="dumbbell-img"
            style={{ top: d.top, left: d.left + cols * spacing }}
          />
        ))}
      </div>
    </div>
  );
};

export default DumbbellBackground;
