import React, { useEffect, useState } from 'react';
import './PositionBar.css';

const PositionBar = ({ landmarks, exercise }) => {
  const [position, setPosition] = useState(50); // 0-100 percentage
  const [phase, setPhase] = useState('up'); // 'up' or 'down'

  useEffect(() => {
    if (!landmarks || landmarks.length === 0) return;

    let percentage = 50;

    if (exercise === 'pushups') {
      // Track shoulder Y position for push-ups
      const leftShoulder = landmarks[11];
      const rightShoulder = landmarks[12];

      if (!leftShoulder || !rightShoulder) return;

      const shoulderY = (leftShoulder.y + rightShoulder.y) / 2;
      // Invert: lower body = lower percentage (down position)
      percentage = Math.max(0, Math.min(100, (1 - shoulderY) * 100));
      setPhase(percentage < 40 ? 'down' : 'up');

    } else if (exercise === 'pullups') {
      // Track shoulder Y position for pull-ups
      const leftShoulder = landmarks[11];
      const rightShoulder = landmarks[12];

      if (!leftShoulder || !rightShoulder) return;

      const shoulderY = (leftShoulder.y + rightShoulder.y) / 2;
      // Higher body = higher percentage (up position)
      percentage = Math.max(0, Math.min(100, (1 - shoulderY) * 100));
      setPhase(percentage > 60 ? 'up' : 'down');

    } else if (exercise === 'squats') {
      // Track hip Y position for squats
      const leftHip = landmarks[23];
      const rightHip = landmarks[24];

      if (!leftHip || !rightHip) return;

      const hipY = (leftHip.y + rightHip.y) / 2;
      // Lower hips = lower percentage (down/squat position)
      percentage = Math.max(0, Math.min(100, (1 - hipY) * 100));
      setPhase(percentage < 45 ? 'down' : 'up');
    }

    setPosition(percentage);
  }, [landmarks, exercise]);

  const getFeedbackText = () => {
    if (exercise === 'pushups') {
      return phase === 'down' ? '⬇️ Go Lower' : '⬆️ Push Up';
    } else if (exercise === 'pullups') {
      return phase === 'up' ? '⬆️ Pull Higher' : '⬇️ Lower Down';
    } else if (exercise === 'squats') {
      return phase === 'down' ? '⬇️ Go Deeper' : '⬆️ Stand Up';
    }
    return '';
  };

  return (
    <div className="position-bar-container">
      <div className="position-bar">
        <div className="bar-track">
          {/* Target zones */}
          <div className="target-zone top-zone"></div>
          <div className="target-zone bottom-zone"></div>

          {/* Moving indicator */}
          <div 
            className={`position-indicator ${phase}`}
            style={{ bottom: `${position}%` }}
          >
            <div className="indicator-dot"></div>
          </div>
        </div>

        {/* Labels */}
        <div className="bar-labels">
          <span className="label-top">UP</span>
          <span className="label-bottom">DOWN</span>
        </div>
      </div>

      {/* Motivational text */}
      <div className="position-feedback">
        {getFeedbackText()}
      </div>
    </div>
  );
};

export default PositionBar;
