import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import ExerciseSelector from './components/ExerciseSelector';
import CameraFeed from './components/CameraFeed';
import WorkoutTracker from './components/WorkoutTracker';

function App() {
  const [selectedExercise, setSelectedExercise] = useState(null);
  const [repCount, setRepCount] = useState(0);
  const [feedback, setFeedback] = useState('');
  const ws = useRef(null);

  useEffect(() => {
    if (selectedExercise) {
      ws.current = new WebSocket("ws://localhost:8765");

      ws.current.onopen = () => {
        console.log("WebSocket connected");
        ws.current.send(JSON.stringify({ type: 'select_exercise', exercise: selectedExercise }));
      };

      ws.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        console.log('Received data from backend:', data);
        if (data.type === 'update') {
          setRepCount(data.rep_count);
          setFeedback(data.feedback);
        }
      };

      ws.current.onclose = () => {
        console.log("WebSocket disconnected");
      };

      return () => {
        if (ws.current) {
          ws.current.close();
        }
      };
    }
  }, [selectedExercise]);

  const handleSelectExercise = (exercise) => {
    setSelectedExercise(exercise);
  };

  const handleGoBack = () => {
    setSelectedExercise(null);
    setRepCount(0);
    setFeedback('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Personal Trainer</h1>
      </header>
      <main>
        {!selectedExercise ? (
          <ExerciseSelector onSelectExercise={handleSelectExercise} />
        ) : (
          <div className="workout-container">
            <div className="camera-container">
              <CameraFeed ws={ws.current} />
              <button onClick={handleGoBack} className="back-button">Back</button>
            </div>
            <WorkoutTracker repCount={repCount} feedback={feedback} />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
