import React from 'react';
import dumbbellImg from './components/dumbbell.png';
import './DumbbellBackground.css';

export default function DumbbellBackground() {
  const dumbbells = [];
  const rows = 30;
  const cols = 60;
  const spacing = 70; // slightly more spacing for bigger dumbbells

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col < cols; col++) {
      const offsetX = row % 2 === 0 ? 0 : spacing / 2;

      // random clustering offsets
      const randomX = Math.random() * 30 - 15;
      const randomY = Math.random() * 30 - 15;

      // random pulse animation values
      const pulseDuration = Math.random() * 1.5 + 1.5; // 1.5–3s
      const pulseDelay = Math.random() * 3; // 0–3s

      dumbbells.push({
        id: `${row}-${col}`,
        top: row * spacing + randomY,
        left: col * spacing - spacing * 2 + offsetX + randomX,
        pulseDuration,
        pulseDelay,
      });
    }
  }

  return (
    <div className="dumbbell-background">
      {/* first layer */}
      <div className="dumbbell-container">
        {dumbbells.map((d) => (
          <img
            key={d.id}
            src={dumbbellImg}
            alt="dumbbell"
            className="dumbbell"
            style={{
              top: `${d.top}px`,
              left: `${d.left}px`,
              animationDuration: `30s, ${d.pulseDuration}s`,
              animationDelay: `0s, ${d.pulseDelay}s`,
            }}
          />
        ))}
      </div>

      {/* second layer for seamless looping */}
      <div
        className="dumbbell-container"
        style={{ transform: `translateX(${spacing}px)` }}
      >
        {dumbbells.map((d) => (
          <img
            key={`dup-${d.id}`}
            src={dumbbellImg}
            alt="dumbbell"
            className="dumbbell"
            style={{
              top: `${d.top}px`,
              left: `${d.left}px`,
              animationDuration: `30s, ${d.pulseDuration}s`,
              animationDelay: `0s, ${d.pulseDelay}s`,
            }}
          />
        ))}
      </div>
    </div>
  );
}
