import React, { useRef, useEffect, useState } from 'react';
import './PongGame.css';

const PongGame = ({ exercise, landmarks, onExit }) => {
  const canvasRef = useRef(null);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [timeAlive, setTimeAlive] = useState(0);
  const [countdown, setCountdown] = useState(10);
  const [gameStarted, setGameStarted] = useState(false);
  const [calibrated, setCalibrated] = useState(false);

  const gameStateRef = useRef({
    ball: { x: 400, y: 300, dx: 4, dy: 4, size: 12 },
    playerPaddle: { x: 20, y: 250, width: 15, height: 100 },
    aiPaddle: { x: 765, y: 250, width: 15, height: 100 },
    score: 0,
    startTime: null,
    animationId: null,
    baselineY: null, // NEW: Store starting position
    rangeY: 0.3 // NEW: Expected range of movement (30% of screen)
  });

  // Countdown timer
  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => {
        setCountdown(countdown - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (countdown === 0 && !gameStarted) {
      setGameStarted(true);
      gameStateRef.current.startTime = Date.now();
    }
  }, [countdown, gameStarted]);

  useEffect(() => {
    if (!gameStarted) return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const gameState = gameStateRef.current;

    // Game loop
    const gameLoop = () => {
      if (gameOver) return;

      // Clear canvas
      ctx.fillStyle = '#000000';
      ctx.fillRect(0, 0, 800, 600);

      // Draw center line
      ctx.strokeStyle = '#a855f7';
      ctx.setLineDash([10, 10]);
      ctx.lineWidth = 2;
      ctx.beginPath();
      ctx.moveTo(400, 0);
      ctx.lineTo(400, 600);
      ctx.stroke();
      ctx.setLineDash([]);

      // Update ball position
      gameState.ball.x += gameState.ball.dx;
      gameState.ball.y += gameState.ball.dy;

      // Ball collision with top/bottom
      if (gameState.ball.y - gameState.ball.size < 0 || 
          gameState.ball.y + gameState.ball.size > 600) {
        gameState.ball.dy *= -1;
      }

      // Ball collision with player paddle
      if (gameState.ball.x - gameState.ball.size < gameState.playerPaddle.x + gameState.playerPaddle.width &&
          gameState.ball.x + gameState.ball.size > gameState.playerPaddle.x &&
          gameState.ball.y > gameState.playerPaddle.y &&
          gameState.ball.y < gameState.playerPaddle.y + gameState.playerPaddle.height) {
        gameState.ball.dx = Math.abs(gameState.ball.dx);
        gameState.score++;
        setScore(gameState.score);

        // Speed up slightly
        gameState.ball.dx *= 1.05;
        gameState.ball.dy *= 1.05;
      }

      // Ball collision with AI paddle (perfect)
      if (gameState.ball.x + gameState.ball.size > gameState.aiPaddle.x &&
          gameState.ball.x - gameState.ball.size < gameState.aiPaddle.x + gameState.aiPaddle.width &&
          gameState.ball.y > gameState.aiPaddle.y &&
          gameState.ball.y < gameState.aiPaddle.y + gameState.aiPaddle.height) {
        gameState.ball.dx = -Math.abs(gameState.ball.dx);
      }

      // AI follows ball perfectly (unbeatable)
      const aiCenter = gameState.aiPaddle.y + gameState.aiPaddle.height / 2;
      const diff = gameState.ball.y - aiCenter;
      gameState.aiPaddle.y += diff * 0.3;

      // Keep AI paddle in bounds
      gameState.aiPaddle.y = Math.max(0, Math.min(600 - gameState.aiPaddle.height, gameState.aiPaddle.y));

      // Check if player lost
      if (gameState.ball.x < 0) {
        setGameOver(true);
        const timeAliveSeconds = Math.floor((Date.now() - gameState.startTime) / 1000);
        setTimeAlive(timeAliveSeconds);
        return;
      }

      // Reset ball if goes past AI (shouldn't happen, but safety)
      if (gameState.ball.x > 800) {
        gameState.ball.x = 400;
        gameState.ball.y = 300;
        gameState.ball.dx = -4;
        gameState.ball.dy = 4;
      }

      // Draw player paddle
      ctx.fillStyle = '#a855f7';
      ctx.shadowBlur = 20;
      ctx.shadowColor = '#a855f7';
      ctx.fillRect(
        gameState.playerPaddle.x,
        gameState.playerPaddle.y,
        gameState.playerPaddle.width,
        gameState.playerPaddle.height
      );

      // Draw AI paddle
      ctx.fillStyle = '#ffffff';
      ctx.shadowBlur = 10;
      ctx.shadowColor = '#ffffff';
      ctx.fillRect(
        gameState.aiPaddle.x,
        gameState.aiPaddle.y,
        gameState.aiPaddle.width,
        gameState.aiPaddle.height
      );

      // Draw ball
      ctx.beginPath();
      ctx.arc(gameState.ball.x, gameState.ball.y, gameState.ball.size, 0, Math.PI * 2);
      ctx.fillStyle = '#c084fc';
      ctx.shadowBlur = 25;
      ctx.shadowColor = '#c084fc';
      ctx.fill();

      // Reset shadow
      ctx.shadowBlur = 0;

      // Draw score
      ctx.fillStyle = '#a855f7';
      ctx.font = 'bold 48px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(gameState.score, 400, 60);

      gameState.animationId = requestAnimationFrame(gameLoop);
    };

    gameLoop();

    return () => {
      if (gameState.animationId) {
        cancelAnimationFrame(gameState.animationId);
      }
    };
  }, [gameOver, gameStarted]);

  // Update player paddle based on body position - CALIBRATED
  useEffect(() => {
    if (!landmarks || landmarks.length === 0 || gameOver) return;

    const gameState = gameStateRef.current;
    let yPosition = 0.5; // Default middle

    if (exercise === 'pushups' || exercise === 'pullups') {
      // Use shoulder Y position
      const leftShoulder = landmarks[11];
      const rightShoulder = landmarks[12];
      if (leftShoulder && rightShoulder) {
        yPosition = (leftShoulder.y + rightShoulder.y) / 2;
      }
    } else if (exercise === 'squats') {
      // Use hip Y position
      const leftHip = landmarks[23];
      const rightHip = landmarks[24];
      if (leftHip && rightHip) {
        yPosition = (leftHip.y + rightHip.y) / 2;
      }
    }

    // Calibrate baseline position when game starts
    if (gameStarted && !calibrated) {
      gameState.baselineY = yPosition;
      setCalibrated(true);
      console.log('Calibrated starting position:', yPosition);
    }

    // Map position relative to baseline
    if (gameState.baselineY !== null) {
      const canvasHeight = 600;
      const paddleHeight = gameState.playerPaddle.height;

      // Calculate offset from baseline
      const offset = yPosition - gameState.baselineY;

      // Map offset to full canvas range
      // Assuming movement range is about 30% of screen in either direction
      const normalizedOffset = offset / gameState.rangeY;

      // Clamp to -1 to 1 range, then map to 0-1
      const clampedNormalized = Math.max(-1, Math.min(1, normalizedOffset));
      const mappedPosition = (clampedNormalized + 1) / 2; // Convert -1..1 to 0..1

      // Map to canvas with baseline at top
      gameState.playerPaddle.y = mappedPosition * (canvasHeight - paddleHeight);
    }
  }, [landmarks, exercise, gameOver, gameStarted, calibrated]);

  const exerciseName = exercise === 'pushups' ? 'Push-Up' : exercise === 'pullups' ? 'Pull-Up' : 'Squat';

  return (
    <div className="pong-game">
      {countdown > 0 ? (
        <div className="game-countdown">
          <h2 className="countdown-title">Get Ready!</h2>
          <div className="countdown-circle">
            <div className="countdown-number">{countdown}</div>
          </div>
          <p className="countdown-instruction">
            Get into your starting {exerciseName} position
          </p>
          <p className="countdown-warning">
            ⚠️ Your current position will be the TOP of the paddle range!
          </p>
        </div>
      ) : !gameOver ? (
        <>
          <div className="game-header">
            <h2 className="game-title">{exerciseName} Challenge</h2>
            <p className="game-instruction">Move down to move paddle down, back up to move paddle up!</p>
          </div>
          <canvas 
            ref={canvasRef} 
            width={800} 
            height={600}
            className="pong-canvas"
          />
          <button className="exit-button" onClick={onExit}>
            Exit Game
          </button>
        </>
      ) : (
        <div className="game-over">
          <h2 className="game-over-title">Game Over!</h2>
          <div className="game-stats">
            <div className="stat">
              <span className="stat-label">Score</span>
              <span className="stat-value">{score}</span>
            </div>
            <div className="stat">
              <span className="stat-label">Time Survived</span>
              <span className="stat-value">{timeAlive}s</span>
            </div>
          </div>
          <button className="play-again-button" onClick={() => window.location.reload()}>
            Play Again
          </button>
          <button className="exit-button" onClick={onExit}>
            Back to Menu
          </button>
        </div>
      )}
    </div>
  );
};

export default PongGame;
