import React, { useState } from 'react';
import './WorkoutTracker.css';

const WorkoutTracker = ({ repCount, feedback, exercise, onDone, onRestart, onSeeResults }) => {
  const [showRestartOptions, setShowRestartOptions] = useState(false);

  let exerciseName;
  switch (exercise) {
    case 'pushups':
      exerciseName = 'Push-Ups';
      break;
    case 'pullups':
      exerciseName = 'Pull-Ups';
      break;
    case 'squats':
      exerciseName = 'Squats';
      break;
    default:
      exerciseName = 'Exercise';
  }

  // Corrected feedback formatter
  const formatFeedback = (feedbackString) => {
    // Only default to 'Good job!' if feedback is null, undefined, or empty string
    if (feedbackString === null || feedbackString === undefined || feedbackString === '') {
      return 'Good job!';
    }
    return feedbackString.replace(/,/g, ' •');
  };

  const handleRestartClick = () => setShowRestartOptions(true);
  const handleDelete = () => {
    setShowRestartOptions(false);
    if (onRestart) onRestart(); // call parent restart logic
  };
  const handleSeeResults = () => {
    setShowRestartOptions(false);
    if (onSeeResults) onSeeResults(); // call parent summary logic
  };

  return (
    <div className="workout-tracker">
      <div className="tracker-header">
        <h3>{exerciseName}</h3>
      </div>

      <div className="rep-counter">
        <div className="rep-label">REPS</div>
        <div className="rep-number">{repCount}</div>
      </div>

      <div className="feedback-section">
        <div className="feedback-label">FORM CHECK</div>
        {/* Use !! to check if there is actual feedback */}
        <div className={`feedback-text ${feedback && feedback.length > 0 ? 'has-feedback' : 'good-form'}`}>
          {formatFeedback(feedback)}
        </div>
      </div>

      {/* Restart Button */}
      {!showRestartOptions && (
        <button className="done-button" onClick={handleRestartClick}>
          Restart
        </button>
      )}

      {/* Restart Options */}
      {showRestartOptions && (
        <div className="restart-options">
          <p>Do you want to delete this session or see results?</p>
          <div className="restart-buttons">
            <button className="delete-button" onClick={handleDelete}>
              Delete
            </button>
            <button className="results-button" onClick={handleSeeResults}>
              See Results
            </button>
          </div>
        </div>
      )}

      {/* Finish Workout Button */}
      {!showRestartOptions && (
        <button className="done-button" onClick={onDone}>
          Finish Workout
        </button>
      )}
    </div>
  );
};

export default WorkoutTracker;
